// Definice glob√°ln√≠ch funkc√≠
const fSetConfigProperty = global.get('setConfigProperty');

const sBuffer = msg.payload; // naƒçten√≠ vstupu

// P≈ôidejte kontrolu, zda je `sBuffer` typu string
if (typeof sBuffer !== 'string') {
    if (debug) {
        node.error("Vstup nen√≠ platn√Ω JWT token (nen√≠ ≈ôetƒõzec).");
    }
    return null;
}

const result = verifyJWTWithoutSecret(sBuffer); // kontrola na platn√Ω token

fSetConfigProperty("bearerToken", result); // ulo≈æit data do glob√°ln√≠ struktury


// Debugging
if (debug) {
    node.warn("bearerToken: Vstup z inputu: " + sBuffer);
    node.warn("bearerToken: V√Ωstup z kontroly platnosti tokenu: " + result);
}

return null;

// Funkce pro dek√≥dov√°n√≠ base64url k√≥dovan√Ωch ≈ôetƒõzc≈Ø
function base64urlDecode(str) {
    // Base64url na base64 konverzi
    str = str.replace(/-/g, '+').replace(/_/g, '/');

    // Dek√≥dov√°n√≠ base64
    return Buffer.from(str, 'base64').toString('utf-8');
}

// Funkce pro ovƒõ≈ôen√≠ JWT tokenu bez tajn√©ho kl√≠ƒçe (z√°kladn√≠ ovƒõ≈ôen√≠)
// Tento k√≥d pouze zkontroluje, zda je token ve spr√°vn√©m form√°tu a zda jsou header a payload platn√© JSON objekty, nikoliv PODPIS!!!
function verifyJWTWithoutSecret(token) {
    // Kontrola, zda je token typu string
    if (typeof token !== 'string') {
        if (debug) {
            node.error("Token nen√≠ platn√Ω ≈ôetƒõzec.");
        }
        return 0;  // Neplatn√Ω form√°t tokenu
    }

    // Rozdƒõlen√≠ JWT tokenu na jeho ƒç√°sti
    const parts = token.split('.');

    // Kontrola, zda m√° token spr√°vn√Ω poƒçet ƒç√°st√≠
    if (parts.length !== 3) {
        return 0;  // Neplatn√Ω form√°t tokenu
    }

    try {
        // Dek√≥dov√°n√≠ header a payload ƒç√°sti tokenu
        const header = JSON.parse(base64urlDecode(parts[0]));
        const payload = JSON.parse(base64urlDecode(parts[1]));

        // Pokud je dek√≥dov√°n√≠ √∫spƒõ≈°n√©, vr√°tit p≈Øvodn√≠ token
        return token;
    } catch (e) {
        // Pokud do≈°lo k chybƒõ bƒõhem dek√≥dov√°n√≠, vr√°tit 0
        return 0;
    }
}

