// Funkce pro form√°tov√°n√≠ uptime do krat≈°√≠ho form√°tu "Xd Yh Zm"
function formatUptimeShort(seconds) {
    const days = Math.floor(seconds / (3600 * 24));
    const hours = Math.floor((seconds % (3600 * 24)) / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = Math.floor(seconds % 60);

    // Sestav√≠me krat≈°√≠ uptime string
    let uptimeString = "";
    if (days > 0) uptimeString += `${days}d `;
    if (hours > 0 || days > 0) uptimeString += `${hours}h `;
    uptimeString += `${minutes}m ${secs}s`;  // Sekundy p≈ôid√°me v≈ædy pro p≈ôesnost

    return uptimeString.trim();
}

// Naƒçten√≠ start time z glob√°ln√≠ promƒõnn√©
