    msg.payload = "Uptime nen√≠ dostupn√Ω. Start time nen√≠ nastaven.";
    return msg;
}

// V√Ωpoƒçet uptime
const now = Date.now();
const uptimeSeconds = Math.floor((now - startTime) / 1000); // rozd√≠l v sekund√°ch

// P≈ôevod uptime na krat≈°√≠ form√°t "Xd Yh Zm"
const uptimeText = formatUptimeShort(uptimeSeconds);

msg.payload = uptimeText;
msg.fcolor = "white"; // Barva textu, m≈Ø≈æete zmƒõnit dle pot≈ôeby

