const debug = false;  // Debugging flag

// Funkce pro pr≈Øbƒõ≈æn√© sƒç√≠t√°n√≠ odebr≈Ø a dod√°vek
// Naƒçten√≠ aktu√°ln√≠ch hodnot z glob√°ln√≠ch promƒõnn√Ωch
let grid1 = global.get('grid1') || 0;
let grid2 = global.get('grid2') || 0;
let grid3 = global.get('grid3') || 0;

let phase1 = global.get('phase1') || 0;
let phase2 = global.get('phase2') || 0;
let phase3 = global.get('phase3') || 0;

// Naƒçten√≠ aktu√°ln√≠ch hodnot z glob√°ln√≠ch promƒõnn√Ωch pro kumulativn√≠ v√Ωpoƒçty
let grid_input_L1_Wh = global.get('grid_input_L1_Wh') || 0;
let grid_output_L1_Wh = global.get('grid_output_L1_Wh') || 0;
let grid_input_L2_Wh = global.get('grid_input_L2_Wh') || 0;
let grid_output_L2_Wh = global.get('grid_output_L2_Wh') || 0;
let grid_input_L3_Wh = global.get('grid_input_L3_Wh') || 0;
let grid_output_L3_Wh = global.get('grid_output_L3_Wh') || 0;

let output_L1_Wh = global.get('output_L1_Wh') || 0;
let output_L2_Wh = global.get('output_L2_Wh') || 0;
let output_L3_Wh = global.get('output_L3_Wh') || 0;

// V√Ωpoƒçet odebr≈Ø a dod√°vek na jednotliv√Ωch f√°z√≠ch pro grid
if (grid1 > 0) {
    grid_input_L1_Wh += grid1 / 3600;
} else {
    grid_output_L1_Wh += Math.abs(grid1) / 3600;
}

if (grid2 > 0) {
    grid_input_L2_Wh += grid2 / 3600;
} else {
    grid_output_L2_Wh += Math.abs(grid2) / 3600;
}

if (grid3 > 0) {
    grid_input_L3_Wh += grid3 / 3600;
} else {
    grid_output_L3_Wh += Math.abs(grid3) / 3600;
}

// V√Ωpoƒçet odebr≈Ø a dod√°vek na jednotliv√Ωch f√°z√≠ch pro v√Ωstup
if (phase1 > 0) {
    output_L1_Wh += phase1 / 3600;
}

if (phase2 > 0) {
