global.set('output_L1_Wh', output_L1_Wh);
global.set('output_L2_Wh', output_L2_Wh);
global.set('output_L3_Wh', output_L3_Wh);

// Debugovac√≠ v√Ωstup
if (debug) {
    node.warn(`F√°ze 1 (Grid): Vstup: ${grid_input_L1_Wh.toFixed(2)} Wh, V√Ωstup: ${grid_output_L1_Wh.toFixed(2)} Wh`);
    node.warn(`F√°ze 2 (Grid): Vstup: ${grid_input_L2_Wh.toFixed(2)} Wh, V√Ωstup: ${grid_output_L2_Wh.toFixed(2)} Wh`);
    node.warn(`F√°ze 3 (Grid): Vstup: ${grid_input_L3_Wh.toFixed(2)} Wh, V√Ωstup: ${grid_output_L3_Wh.toFixed(2)} Wh`);
    node.warn(`F√°ze 1 (V√Ωstup): V√Ωstup: ${output_L1_Wh.toFixed(2)} Wh`);
    node.warn(`F√°ze 2 (V√Ωstup): V√Ωstup: ${output_L2_Wh.toFixed(2)} Wh`);
    node.warn(`F√°ze 3 (V√Ωstup): V√Ωstup: ${output_L3_Wh.toFixed(2)} Wh`);
}

// P≈ôipojen√≠ v√Ωsledk≈Ø k zpr√°vƒõ
msg.payload = {
    grid_input_L1_Wh: grid_input_L1_Wh.toFixed(2),
    grid_output_L1_Wh: grid_output_L1_Wh.toFixed(2),
    grid_input_L2_Wh: grid_input_L2_Wh.toFixed(2),
    grid_output_L2_Wh: grid_output_L2_Wh.toFixed(2),
    grid_input_L3_Wh: grid_input_L3_Wh.toFixed(2),
    grid_output_L3_Wh: grid_output_L3_Wh.toFixed(2),
    output_L1_Wh: output_L1_Wh.toFixed(2),
    output_L2_Wh: output_L2_Wh.toFixed(2),
    output_L3_Wh: output_L3_Wh.toFixed(2)
};

return msg;

