//*****************************************************************************
// Debugging flag
const debug = false;  // Nastavte na true pro zapnut√≠ debug zpr√°v

// Debug: V√Ωpis cel√© zpr√°vy pro diagnostiku
//node.warn("Raw message received: " + JSON.stringify(msg, null, 2));

// Ovƒõ≈ôen√≠, kde se nach√°z√≠ unitID
let unitID = msg.unitId || msg.queueLengthByUnitId?.unitId || msg.modbusRequest?.unitid;

// Pokud st√°le nen√≠ unitID, vyp√≠≈°eme chybu a ukonƒç√≠me zpracov√°n√≠
if (typeof unitID === "undefined") {
    node.warn("Chyba: unitID je undefined!");
    return null;
}

// Naƒçten√≠ glob√°ln√≠ho mappingu senzor≈Ø
let mapping = global.get("sensorMapping") || {};

// Hled√°n√≠ n√°zvu senzoru podle unitID
let customName = "";
for (let key in mapping) {
    if (mapping[key].unitID === unitID) {
        customName = mapping[key].customName;
        break;
    }
}

// Pokud jsme nena≈°li odpov√≠daj√≠c√≠ jm√©no senzoru, vyp√≠≈°eme varov√°n√≠ a ukonƒç√≠me
if (!customName) {
    node.warn(`Variable name for unitID ${unitID} not recognized!`);
    return null;
}

// Nahrazen√≠ slov "Menic" a "Teplota" slovem "Temperature"
customName = customName.replace(/Menic|Teplota/g, "Temperature");

// Ovƒõ≈ôen√≠ a zpracov√°n√≠ hodnoty teploty
let rawValue = msg.payload?.[0];  // Prvn√≠ hodnota v payload poli
if (typeof rawValue === "undefined") {
    node.warn(`Chyba: Hodnota pro unitID ${unitID} je undefined!`);
    return null;
}

let value = rawValue / 100;  // P≈ôevod na ¬∞C (nap≈ô. 3700 ‚Üí 37.00¬∞C)

// Oprava n√°zvu pro glob√°ln√≠ promƒõnnou
let sanitizedName = customName.replace(/\s+/g, "_"); // Nahrazen√≠ mezer podtr≈æ√≠tky

// Debugging v√Ωpis
if (debug) {
    //node.warn(`unitID: ${unitID}, value: ${value}¬∞C, customName: ${customName} -> saved as: ${sanitizedName}`);
    node.warn(`SanitizedName: ` + sanitizedName + `, Value: ` + value);
}

// Ulo≈æen√≠ hodnoty do glob√°ln√≠ promƒõnn√©
global.set(sanitizedName, value);

// V√Ωstup zpr√°vy pro dal≈°√≠ zpracov√°n√≠
msg.payload = {
    unitID: unitID,
    value: value,
    customName: customName
};

return msg;

