// Nastaven√≠ ƒçasov√Ωch interval≈Ø v milisekund√°ch - pro snadnou √∫pravu
const INTERVAL_OKAMZITY = 0;          // Okam≈æit√Ω v√Ωstup (p≈ôi ka≈æd√©m tiknut√≠)
const INTERVAL_MIN1 = 60000;          // 1 minuta
const INTERVAL_MIN2 = 120000;         // 2 minuty
const INTERVAL_MIN15 = 900000;        // 15 minut

// Inicializace ƒçasov√Ωch znaƒçek, pokud neexistuj√≠
if (context.lastSent === undefined) {
    context.lastSent = {
        okamzity: 0,
        min1: 0,
        min2: 0,
        min15: 0
    };
    context.firstRun = true;  // P≈ô√≠znak prvn√≠ho spu≈°tƒõn√≠

    // P≈ôi prvn√≠m spu≈°tƒõn√≠ vr√°tit null pro v≈°echny v√Ωstupy
    return [null, null, null, null];
}

const now = Date.now();
let outputs = [null, null, null, null]; // V√Ωchoz√≠ hodnoty - nic nepos√≠lat

// Po prvn√≠m spu≈°tƒõn√≠ ji≈æ norm√°lnƒõ pos√≠lat zpr√°vy
if (context.firstRun) {
    context.firstRun = false;
    // Nastavit ƒçasov√© znaƒçky na aktu√°ln√≠ ƒças bez odesl√°n√≠ zpr√°v
    context.lastSent.okamzity = now;
    context.lastSent.min1 = now;
    context.lastSent.min2 = now;
    context.lastSent.min15 = now;
} else {
    // Kontrola pro okam≈æit√Ω v√Ωstup - p≈ôi ka≈æd√©m tiknut√≠
    outputs[0] = { 
        payload: "Okam≈æit√Ω v√Ωstup",
        timestamp: now,
        interval: "okam≈æit√Ω"
    };
    context.lastSent.okamzity = now;

    // Aktualizace stavu uzlu - zobraz√≠ ƒças posledn√≠ho tiknut√≠
    const timeStr = new Date().toLocaleTimeString();
    node.status({fill:"green", shape:"dot", text: "Tik: " + timeStr});

    // Kontrola pro 1 minutov√Ω interval
    if (now - context.lastSent.min1 >= INTERVAL_MIN1) {
        outputs[1] = { 
            payload: "Posun o 1 minutu", 
            timestamp: now,
            interval: "1 minuta" 
        };
        context.lastSent.min1 = now;
    }

    // Kontrola pro 2 minutov√Ω interval
    if (now - context.lastSent.min2 >= INTERVAL_MIN2) {
        outputs[2] = { 
            payload: "Posun o 2 minuty", 
            timestamp: now,
            interval: "2 minuty" 
        };
        context.lastSent.min2 = now;
    }

    // Kontrola pro 15 minutov√Ω interval
    if (now - context.lastSent.min15 >= INTERVAL_MIN15) {
        outputs[3] = { 
            payload: "Posun o 15 minut", 
            timestamp: now,
            interval: "15 minut" 
        };
        context.lastSent.min15 = now;
    }
}

return outputs;

