// Funkce vracej√≠c√≠ konec dne (23:59:59)
global.set('getEndOfDayTimestamp', function getEndOfDayTimestamp() {
    let now = new Date(); // Dne≈°n√≠ datum
    let formatter = new Intl.DateTimeFormat('en-US', {
        timeZone: sTZidentifier,
        year: 'numeric',
        month: 'numeric',
        day: 'numeric',
    });

    // Naform√°tujeme na datum ve spr√°vn√©m ƒçasov√©m p√°smu
    let parts = formatter.formatToParts(now);
    let year = parseInt(parts.find(part => part.type === 'year').value, 10);
    let month = parseInt(parts.find(part => part.type === 'month').value, 10) - 1;
    let day = parseInt(parts.find(part => part.type === 'day').value, 10);

    // Vytvo≈ô√≠me konec dne
    let endOfDay = new Date(year, month, day, 23, 59, 59);

    // Vr√°t√≠me timestamp v sekund√°ch
    return Math.floor(endOfDay.getTime() / 1000);
});

