global.set('convertUnixTimestamp', function(inputTime, debug = true) {
    try {
        if (isNaN(inputTime)) {
            if (debug) { node.error("Invalid time format: " + inputTime);}
            return null;
        }
        let date = new Date(parseInt(inputTime));
        if (isNaN(date.getTime())) {
            if (debug) { node.error("Constructed date is invalid.");}
            return null;
        }
        return date.getTime();
    } catch (error) {
        node.error("Error: " + error.message);
        return null;
    }
});

