// Convert Function Number Signet To Unsignet
global.set('nConvertSignetUnsignet',function nConvertSignetUnsignet(nNumber) {
    if (nNumber > 32767) nNumber -= 65536; 
    return nNumber;
});
//*****************************************************************************
// Convert Function Number Signet To Unsignet for INT32
global.set('nConvertSignetUnsignetINT32', function nConvertSignetUnsignet(nNumber) {
    // Check if the number is greater than the maximum positive value for a signed 32-bit integer
    if (nNumber > 2147483647) {
        // Subtract the range of a 32-bit unsigned integer to convert it to a signed integer
        nNumber -= 4294967296; // 2^32
    }
    return nNumber;
});
//*****************************************************************************
// Convert Function Number Unsignet To Signet
global.set('nConvertUnsignetToSignet', function nConvertUnsignetToSignet(nNumber) {
    if (nNumber < 0) nNumber += 65536;
    return nNumber;
});
//*****************************************************************************
// Funkce pro konverzi bezznam√©nkov√© hodnoty (unsigned int32) na znam√©nkovou hodnotu (signed int32)
global.set('nConvertUnsignetToSignetINT32', function nConvertUnsignetToSignetINT32(nNumber) {
    // Pokud je nejvy≈°≈°√≠ bit nastaven (hodnota >= 2^31)
    if (nNumber > 2147483647) {
        // P≈ôevod na z√°porn√© ƒç√≠slo
        return nNumber - 4294967296; // 4294967296 = 2^32
    }
    return nNumber;
});
