global.set('shiftFilenamePath', function () {
    let currentPaths = global.get("current_filename_paths");

    // Pokud je pracovn√≠ pole pr√°zdn√©, vr√°t√≠me hl√°≈°ku o pr√°zdn√©m poli
    if (!currentPaths || currentPaths.length === 0) {
        //node.warn("Pracovn√≠ pole je pr√°zdn√©, prov√°d√≠m reset.");
        // Reset pole, proto≈æe je pr√°zdn√©
        global.get('resetFilenamePaths')();
        currentPaths = global.get("current_filename_paths");
    }

    // Odebereme prvn√≠ prvek z pole
    const path = currentPaths.shift(); // Odeb√≠r√°me prvn√≠ prvek

    // Ulo≈æ√≠me aktualizovan√© pracovn√≠ pole zpƒõt do glob√°ln√≠ promƒõnn√©
    global.set("current_filename_paths", currentPaths);

    // Pokud bylo pole pr√°zdn√© i po resetu
    if (!path) {
        node.warn("Pole je pr√°zdn√©, ≈æ√°dn√Ω soubor k n√°vratu.");
        return null;
    } else {
        // Vra≈•me odebranou cestu
        return path;
    }
});

